import Player from './player.js';
import Weapon from './weapon.js';

// class Map : génération des obstacles, import des joueurs et des armes
export default class Map {
  constructor(nbLignes, nbColonnes) {
    this.nbLignes = nbLignes;
    this.nbColonnes = nbColonnes;
  }
  creerGrille() {
    let i;
    let j;
    let x = 0;
    let y = 0;

    for (i = 0; i < this.nbLignes; i++) {
      let trElt = document.createElement('tr');
      trElt.id = 'line-' + i;
      document.querySelector('#plateau').appendChild(trElt);

      for (j = 0; j < this.nbColonnes; j++) {
        const tdElt = document.createElement('td');
        tdElt.id = x + '-' + y;
        tdElt.dataset.x = x;
        tdElt.dataset.y = y;
        tdElt.innerHTML = `${i} - ${j}`; // data attribute
        document.getElementById(`line-${y}`).appendChild(tdElt);
        x++; // aller à droite
        // passer à la ligne
        if (document.getElementById(`line-${y}`).children.length == this.nbColonnes) {
          y++;
          x = 0;
        }
      }
    }
  }

  createWalls() {
    const $tdElts = $('td');

    // le nb de mur va être dans cet interval
    const min = 10;
    const max = 15;

    // generation d'un nb aléatoire (voir la fonction dans initGame.js)
    const randomNumber = random(min, max);

    for (let i = 0; i < randomNumber; i++) {
      // sélectionne un td au hasard
      let index = random(0, $tdElts.length);  //index = 0 à 99 taille de la grille
      let randomTdElt = $tdElts[index];

      // fais les vérifications pour qu'un mur n'apparaisse pas sur un autre
      while (this.getCellInfos(randomTdElt.id) !== 0) {
        //3-1
        index = random(0, $tdElts.length);
        randomTdElt = $tdElts[index];
      }

      // on donne la class walls et la couleur grise
      $(randomTdElt).addClass('walls');

      //$(randomTdElt).removeClass("free").addClass("walls");
    }
  }

  createWeapons() {
    //to treat the object like an array
    const arrayWeapons = [];
    for (const element in this.weapons) {
        arrayWeapons.push(this.weapons[element]);
    }

    // remove the last (=default weapon) bcs it dont't need to be displayed on the map
    arrayWeapons.pop();

    // loop 'till the nb of weapons is not achieved
    for (let i = 0; i < this.nbOfWeapons; i++) {
        // selecting a random weapon
        const randomIndexWeapon = random(0, arrayWeapons.length);
        const randomWeapon = arrayWeapons[randomIndexWeapon];

        let elPos = `${randomWeapon.x}-${randomWeapon.y}`;
        // adding attribute "pos"
        randomWeapon.pos = elPos;
        // selecting the cell id by the attribute "pos"
        let tdEltId = $(`#${randomWeapon.pos}`);

        //"if" statement won't work bcs it watch only 1 time and then pass though the verification
        while (this.getCellContent(elPos) !== 0) {
            //picking a new pos
            let newXpos = random(0, this.nbOfLines); // the maximum x(width) grid
            let newYpos = random(0, this.nbOfColumns); // the maximum y(height) grid

            //assignatate the new pos to the weapon
            randomWeapon.x = newXpos;
            randomWeapon.y = newYpos;
            elPos = newXpos + "-" + newYpos;
            randomWeapon.pos = elPos;

            //selecting a new cell to the weapon
            tdEltId = $(`#${elPos}`);
        }
        //giving class & attribute to the weapon's cell
        tdEltId.addClass(`weapon ${randomWeapon.name}`).attr("data-weapon", randomWeapon.name);
    }
}

  // renvoi ce qu'il y a sur la cellule visée
  // parametre pos une position sous la forme {3-1}
  // returne un nombre correspondant à la classe associée ou 0 si rien ne correspond
  
  getCellInfos(pos) {
    // la pos ressemble à 3-1 par exemple
    // on la sélectionne avec jquery
    let posTd = $(`#${pos}`);
    if ($(posTd).hasClass('walls')) return 1;
    else return 0;
  }

  regarderAutour(pos, celluleARegarder = 1) {
    // pos est sous la forme 3-1
    const x = pos.chartAt(0);
    const y = pos.charAt(pos.charAt.length - 1);
  }
}



